mapper.select:
		select `order_no`,`merch_no`,`audit_type`,`audit_result`,`auditor`,`audit_time`,`crt_time`,`memo`,amount,poundage from pay_audit

	  		   and order_no = #{orderNo} 
	  		   and crt_time >= #{beginDate} 
	  		    and crt_time <= #{endDate} 
	  		   and merch_no = #{merchNo} 
	  		   and audit_type = #{auditType} 
	  		   and audit_result = #{auditResult} 



                order by ${sort} ${order}


                order by crt_time desc



			limit #{offset}, #{limit}

		select count(1) from pay_audit

	  	 	 and order_no = #{orderNo} 
  		 	 and crt_time >= #{beginDate} 
  		  	  and crt_time <= #{endDate} 
  		 	 and merch_no = #{merchNo} 
  		  	 and audit_type = #{auditType} 
  		  	 and audit_result = #{auditResult}

mapper.update:
update pay_audit 

	`audit_result` = #{auditResult}, 
	`auditor` = #{auditor}, 
	`audit_time` = #{auditTime}, 
	`memo` = #{memo}

where order_no = #{orderNo} and merch_no = #{merchNo} and audit_type = #{auditType}
