mapper.select:
		select `id`,`merch_no`,`out_channel`,`account_no`,`account_name`,`account_phone`,`service_tel`,`memo`,cost_rate,jf_rate,`api_key` from pay_qr_config

  		   and merch_no = #{merchNo} 
  		   and out_channel = #{outChannel} 
  		   and account_no = #{accountNo} 
  		   and account_name = #{accountName} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(1) from pay_qr_config

  		   and merch_no = #{merchNo} 
  		   and out_channel = #{outChannel} 
  		   and account_no = #{accountNo} 
  		   and account_name = #{accountName}

mapper.update:
update pay_qr_config
`account_name` = #{accountName}, `account_phone` = #{accountPhone}, `account_no` = #{accountNo}, `service_tel` = #{serviceTel}, `memo` = #{memo}, `cost_rate` = #{costRate}, `jf_rate` = #{jfRate}, `api_key` = #{apiKey},
where merch_no = #{merchNo} and out_channel = #{outChannel}
